import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

interface Vehicle {
  _id: string;
  brand: string;
  model: string;
  variant: string;
  category: string;
  fuel_type: string;
  pricing: {
    ex_showroom_inr: number;
    on_road_inr: number;
  };
  images: {
    thumbnail: string;
  };
  ratings: {
    user_rating: number;
  };
}

const HomePage: React.FC = () => {
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [loading, setLoading] = useState(true);
  const [compareList, setCompareList] = useState<string[]>([]);
  const [showCompareModal, setShowCompareModal] = useState(false);
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const navigate = useNavigate();
  const [filters, setFilters] = useState({
    category: '',
    brand: '',
    fuel_type: '',
    min_price: '',
    max_price: '',
    search: '',
  });

  const fetchVehicles = useCallback(async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      Object.entries(filters).forEach(([key, value]) => {
        if (value) params.append(key, value);
      });

      const response = await axios.get(`http://localhost:5000/api/vehicles?${params}`);
      setVehicles(response.data);
    } catch (error) {
      console.error('Error fetching vehicles:', error);
    } finally {
      setLoading(false);
    }
  }, [filters]);

  useEffect(() => {
    fetchVehicles();
    // Load compare list from localStorage
    const savedCompareList = localStorage.getItem('compareList');
    if (savedCompareList) {
      setCompareList(JSON.parse(savedCompareList));
    }
  }, [fetchVehicles]);

  const handleFilterChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value,
    });
  };

  const handleAddToCompare = (vehicleId: string) => {
    if (compareList.includes(vehicleId)) {
      // Remove from compare list
      const newCompareList = compareList.filter(id => id !== vehicleId);
      setCompareList(newCompareList);
      localStorage.setItem('compareList', JSON.stringify(newCompareList));
    } else {
      // Add to compare list
      if (compareList.length >= 4) {
        setShowCompareModal(true);
        return;
      }
      const newCompareList = [...compareList, vehicleId];
      setCompareList(newCompareList);
      localStorage.setItem('compareList', JSON.stringify(newCompareList));
    }
  };

  const handleCompareNow = () => {
    if (compareList.length >= 2) {
      navigate('/compare');
    }
  };

  const clearFilters = () => {
    setFilters({
      category: '',
      brand: '',
      fuel_type: '',
      min_price: '',
      max_price: '',
      search: '',
    });
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(price);
  };

  const activeFiltersCount = Object.values(filters).filter(value => value !== '').length;

  return (
    <div className="min-h-screen bg-gradient-to-br from-sky-50 via-white to-orange-50 pb-20">
      {/* Sidebar Overlay */}
      {sidebarOpen && (
        <div 
          className="sidebar-overlay"
          onClick={() => setSidebarOpen(false)}
          aria-hidden="true"
        />
      )}

      {/* Sidebar */}
      <div className={`sidebar ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}`}>
        <div className="p-6">
          <div className="flex items-center justify-between mb-6">
            <h2 className="text-2xl font-bold text-gray-900">Filters</h2>
            <button
              onClick={() => setSidebarOpen(false)}
              className="text-gray-500 hover:text-gray-700 text-2xl"
              aria-label="Close filters"
            >
              ×
            </button>
          </div>

          <div className="filter-section">
            <div className="space-y-6">
              {/* Search */}
              <div>
                <label htmlFor="search" className="block text-sm font-semibold text-gray-700 mb-2">
                  Search Vehicles
                </label>
                <input
                  id="search"
                  type="text"
                  name="search"
                  value={filters.search}
                  onChange={handleFilterChange}
                  placeholder="Search by brand, model..."
                  className="input-field"
                  aria-describedby="search-help"
                />
                <p id="search-help" className="text-xs text-gray-500 mt-1">
                  Search by brand, model, or variant
                </p>
              </div>

              {/* Category */}
              <div>
                <label htmlFor="category" className="block text-sm font-semibold text-gray-700 mb-2">
                  Category
                </label>
                <select
                  id="category"
                  name="category"
                  value={filters.category}
                  onChange={handleFilterChange}
                  className="input-field"
                >
                  <option value="">All Categories</option>
                  <option value="Bike">Bike</option>
                  <option value="Scooter">Scooter</option>
                  <option value="EV">Electric Vehicle</option>
                </select>
              </div>

              {/* Brand */}
              <div>
                <label htmlFor="brand" className="block text-sm font-semibold text-gray-700 mb-2">
                  Brand
                </label>
                <select
                  id="brand"
                  name="brand"
                  value={filters.brand}
                  onChange={handleFilterChange}
                  className="input-field"
                >
                  <option value="">All Brands</option>
                  <option value="Honda">Honda</option>
                  <option value="Yamaha">Yamaha</option>
                  <option value="TVS">TVS</option>
                  <option value="Ola">Ola</option>
                  <option value="Royal Enfield">Royal Enfield</option>
                </select>
              </div>

              {/* Fuel Type */}
              <div>
                <label htmlFor="fuel_type" className="block text-sm font-semibold text-gray-700 mb-2">
                  Fuel Type
                </label>
                <select
                  id="fuel_type"
                  name="fuel_type"
                  value={filters.fuel_type}
                  onChange={handleFilterChange}
                  className="input-field"
                >
                  <option value="">All Fuel Types</option>
                  <option value="Petrol">Petrol</option>
                  <option value="Electric">Electric</option>
                  <option value="Diesel">Diesel</option>
                </select>
              </div>

              {/* Price Range */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label htmlFor="min_price" className="block text-sm font-semibold text-gray-700 mb-2">
                    Min Price
                  </label>
                  <input
                    id="min_price"
                    type="number"
                    name="min_price"
                    value={filters.min_price}
                    onChange={handleFilterChange}
                    placeholder="Min price"
                    className="input-field"
                    aria-label="Minimum price"
                  />
                </div>
                <div>
                  <label htmlFor="max_price" className="block text-sm font-semibold text-gray-700 mb-2">
                    Max Price
                  </label>
                  <input
                    id="max_price"
                    type="number"
                    name="max_price"
                    value={filters.max_price}
                    onChange={handleFilterChange}
                    placeholder="Max price"
                    className="input-field"
                    aria-label="Maximum price"
                  />
                </div>
              </div>

              {/* Clear Filters */}
              <div className="pt-4 border-t border-sky-200">
                <button
                  onClick={clearFilters}
                  className="w-full btn-outline"
                  aria-label="Clear all filters"
                >
                  Clear All Filters
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className={`transition-all duration-300 ${sidebarOpen ? 'ml-80' : 'ml-0'}`}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Header */}
          <div className="mb-8">
            <div className="flex items-center justify-between mb-6">
              <div>
                <h1 className="text-4xl font-bold text-gray-900 mb-2">
                  Browse Vehicles
                </h1>
                <p className="text-lg text-gray-600">
                  Discover your perfect ride from our curated collection
                </p>
              </div>
              <button
                onClick={() => setSidebarOpen(true)}
                className="btn-secondary flex items-center space-x-2"
                aria-label="Open filters"
              >
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 4a1 1 0 011-1h16a1 1 0 011 1v2.586a1 1 0 01-.293.707l-6.414 6.414a1 1 0 00-.293.707V17l-4 4v-6.586a1 1 0 00-.293-.707L3.293 7.293A1 1 0 013 6.586V4z" />
                </svg>
                <span>Filters</span>
                {activeFiltersCount > 0 && (
                  <span className="badge badge-primary">
                    {activeFiltersCount}
                  </span>
                )}
              </button>
            </div>
          </div>

          {/* Vehicles Grid */}
          {loading ? (
            <div className="flex justify-center items-center py-20">
              <div className="loading-spinner"></div>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
              {vehicles.map((vehicle) => (
                <div key={vehicle._id} className="vehicle-card card-hover">
                  <div className="relative overflow-hidden">
                    <img
                      src={vehicle.images.thumbnail}
                      alt={`${vehicle.brand} ${vehicle.model}`}
                      className="vehicle-card-image"
                      loading="lazy"
                    />
                    <div className="absolute top-4 right-4">
                      <button
                        onClick={() => handleAddToCompare(vehicle._id)}
                        className={`compare-btn ${compareList.includes(vehicle._id) ? 'active' : ''}`}
                        aria-label={`${compareList.includes(vehicle._id) ? 'Remove from' : 'Add to'} comparison`}
                      >
                        {compareList.includes(vehicle._id) ? '✓ Added' : 'Compare'}
                      </button>
                    </div>
                    <div className="absolute top-4 left-4">
                      <span className="badge badge-secondary">
                        {vehicle.category}
                      </span>
                    </div>
                  </div>
                  
                  <div className="p-6">
                    <div className="mb-4">
                      <h3 className="text-xl font-bold text-gray-900 mb-1">
                        {vehicle.brand} {vehicle.model}
                      </h3>
                      <p className="text-gray-600 mb-2">{vehicle.variant}</p>
                      <div className="flex items-center space-x-2">
                        <span className="badge badge-primary">
                          {vehicle.fuel_type}
                        </span>
                        <div className="flex items-center">
                          <span className="rating-star">★</span>
                          <span className="ml-1 text-sm font-medium text-gray-600">
                            {vehicle.ratings.user_rating.toFixed(1)}
                          </span>
                        </div>
                      </div>
                    </div>
                    
                    <div className="mb-6">
                      <div className="price-display mb-1">
                        {formatPrice(vehicle.pricing.ex_showroom_inr)}
                      </div>
                      <p className="text-sm text-gray-500">Ex-showroom price</p>
                    </div>
                    
                    <div className="flex space-x-3">
                      <Link
                        to={`/vehicle/${vehicle._id}`}
                        className="flex-1 btn-primary text-center"
                      >
                        View Details
                      </Link>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
          
          {!loading && vehicles.length === 0 && (
            <div className="text-center py-20">
              <div className="max-w-md mx-auto">
                <div className="w-24 h-24 mx-auto mb-6 bg-gradient-to-br from-sky-100 to-orange-100 rounded-full flex items-center justify-center">
                  <svg className="w-12 h-12 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9.172 16.172a4 4 0 015.656 0M9 12h6m-6-4h6m2 5.291A7.962 7.962 0 0112 15c-2.34 0-4.29-1.009-5.824-2.709M15 6.291A7.962 7.962 0 0012 4c-2.34 0-4.29 1.009-5.824 2.709" />
                  </svg>
                </div>
                <h3 className="text-xl font-semibold text-gray-900 mb-2">No vehicles found</h3>
                <p className="text-gray-600 mb-6">
                  Try adjusting your filters to find more vehicles.
                </p>
                <button
                  onClick={clearFilters}
                  className="btn-primary"
                >
                  Clear Filters
                </button>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Compare Button */}
      {compareList.length > 0 && (
        <div className="fixed bottom-6 right-6 z-50">
          <div className="bg-white rounded-2xl shadow-2xl p-6 border-2 border-orange-200 max-w-sm">
            <div className="flex items-center space-x-4">
              <div className="flex-1">
                <p className="text-sm font-semibold text-gray-900 mb-1">
                  {compareList.length} vehicle{compareList.length > 1 ? 's' : ''} selected
                </p>
                <p className="text-xs text-gray-500">Maximum 4 vehicles</p>
              </div>
              <button
                onClick={handleCompareNow}
                disabled={compareList.length < 2}
                className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
                aria-label={`Compare ${compareList.length} vehicles`}
              >
                Compare Now
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Compare Modal */}
      {showCompareModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
          <div className="bg-white rounded-2xl p-8 max-w-md w-full">
            <div className="text-center">
              <div className="w-16 h-16 mx-auto mb-4 bg-gradient-to-br from-orange-100 to-orange-200 rounded-full flex items-center justify-center">
                <svg className="w-8 h-8 text-orange-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 9v2m0 4h.01m-6.938 4h13.856c1.54 0 2.502-1.667 1.732-2.5L13.732 4c-.77-.833-1.964-.833-2.732 0L3.732 16.5c-.77.833.192 2.5 1.732 2.5z" />
                </svg>
              </div>
              <h3 className="text-xl font-bold text-gray-900 mb-2">
                Maximum 4 vehicles can be compared
              </h3>
              <p className="text-gray-600 mb-6">
                Please remove a vehicle from your comparison list to add a new one.
              </p>
              <div className="flex space-x-3">
                <button
                  onClick={() => setShowCompareModal(false)}
                  className="flex-1 btn-secondary"
                >
                  Cancel
                </button>
                <button
                  onClick={() => {
                    setShowCompareModal(false);
                    navigate('/compare');
                  }}
                  className="flex-1 btn-primary"
                >
                  Go to Compare
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HomePage;
