import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

interface Vehicle {
  _id: string;
  brand: string;
  model: string;
  variant: string;
  category: string;
  fuel_type: string;
  pricing: {
    ex_showroom_inr: number;
    on_road_inr: number;
  };
  images: {
    thumbnail: string;
  };
  ratings: {
    user_rating: number;
  };
  performance: {
    top_speed_kmph: number;
    mileage_kmpl: number;
  };
}

const ComparePage: React.FC = () => {
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [loading, setLoading] = useState(false);
  const [selectedIds, setSelectedIds] = useState<string[]>([]);
  const navigate = useNavigate();

  const fetchVehicles = async (ids: string[]) => {
    if (ids.length < 2) return;
    
    try {
      setLoading(true);
      const response = await axios.post('http://localhost:5000/api/vehicles/compare', {
        ids: ids,
      });
      setVehicles(response.data);
    } catch (error) {
      console.error('Error fetching vehicles for comparison:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    // Load compare list from localStorage
    const savedCompareList = localStorage.getItem('compareList');
    if (savedCompareList) {
      const compareIds = JSON.parse(savedCompareList);
      setSelectedIds(compareIds);
      if (compareIds.length > 0) {
        fetchVehicles(compareIds);
      }
    }
  }, []);

  useEffect(() => {
    if (selectedIds.length > 0) {
      fetchVehicles(selectedIds);
    }
  }, [selectedIds]);

  const handleRemoveFromCompare = (vehicleId: string) => {
    const newSelectedIds = selectedIds.filter(id => id !== vehicleId);
    setSelectedIds(newSelectedIds);
    localStorage.setItem('compareList', JSON.stringify(newSelectedIds));
    
    // If less than 2 vehicles, redirect to home
    if (newSelectedIds.length < 2) {
      navigate('/home');
    }
  };

  const handleClearAll = () => {
    setSelectedIds([]);
    setVehicles([]);
    localStorage.removeItem('compareList');
    navigate('/home');
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(price);
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Compare Vehicles</h1>
        <p className="text-gray-600">
          Select 2-4 vehicles to compare their specifications side by side.
        </p>
      </div>

      {selectedIds.length === 0 && (
        <div className="text-center py-12">
          <p className="text-gray-500 text-lg mb-4">
            No vehicles selected for comparison.
          </p>
          <Link to="/home" className="btn-primary">
            Browse Vehicles
          </Link>
        </div>
      )}

      {selectedIds.length > 0 && selectedIds.length < 2 && (
        <div className="text-center py-12">
          <p className="text-gray-500 text-lg mb-4">
            Please select at least 2 vehicles to compare.
          </p>
          <Link to="/home" className="btn-primary">
            Browse Vehicles
          </Link>
        </div>
      )}

      {loading && (
        <div className="flex justify-center items-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
        </div>
      )}

      {vehicles.length > 0 && (
        <div className="mb-6">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-2xl font-bold text-gray-900">
              Compare {vehicles.length} Vehicle{vehicles.length > 1 ? 's' : ''}
            </h2>
            <button
              onClick={handleClearAll}
              className="btn-secondary text-red-600 hover:bg-red-50"
            >
              Clear All
            </button>
          </div>
          
          <div className="overflow-x-auto">
            <table className="w-full border-collapse border border-gray-300">
              <thead>
                <tr className="bg-gray-50">
                  <th className="border border-gray-300 p-4 text-left">Specification</th>
                  {vehicles.map((vehicle) => (
                    <th key={vehicle._id} className="border border-gray-300 p-4 text-center relative">
                      <button
                        onClick={() => handleRemoveFromCompare(vehicle._id)}
                        className="absolute top-2 right-2 text-red-500 hover:text-red-700 text-lg font-bold"
                        title="Remove from comparison"
                      >
                        ×
                      </button>
                      <div className="flex flex-col items-center">
                        <img
                          src={vehicle.images.thumbnail}
                          alt={`${vehicle.brand} ${vehicle.model}`}
                          className="w-24 h-16 object-cover rounded mb-2"
                        />
                        <h3 className="font-semibold text-sm">
                          {vehicle.brand} {vehicle.model}
                        </h3>
                        <p className="text-xs text-gray-600">{vehicle.variant}</p>
                      </div>
                    </th>
                  ))}
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td className="border border-gray-300 p-4 font-semibold">Category</td>
                  {vehicles.map((vehicle) => (
                    <td key={vehicle._id} className="border border-gray-300 p-4 text-center">
                      {vehicle.category}
                    </td>
                  ))}
                </tr>
                <tr>
                  <td className="border border-gray-300 p-4 font-semibold">Fuel Type</td>
                  {vehicles.map((vehicle) => (
                    <td key={vehicle._id} className="border border-gray-300 p-4 text-center">
                      {vehicle.fuel_type}
                    </td>
                  ))}
                </tr>
                <tr>
                  <td className="border border-gray-300 p-4 font-semibold">Ex-showroom Price</td>
                  {vehicles.map((vehicle) => (
                    <td key={vehicle._id} className="border border-gray-300 p-4 text-center font-semibold text-primary-600">
                      {formatPrice(vehicle.pricing.ex_showroom_inr)}
                    </td>
                  ))}
                </tr>
                <tr>
                  <td className="border border-gray-300 p-4 font-semibold">On-road Price</td>
                  {vehicles.map((vehicle) => (
                    <td key={vehicle._id} className="border border-gray-300 p-4 text-center font-semibold text-primary-600">
                      {formatPrice(vehicle.pricing.on_road_inr)}
                    </td>
                  ))}
                </tr>
                <tr>
                  <td className="border border-gray-300 p-4 font-semibold">Top Speed</td>
                  {vehicles.map((vehicle) => (
                    <td key={vehicle._id} className="border border-gray-300 p-4 text-center">
                      {vehicle.performance.top_speed_kmph} kmph
                    </td>
                  ))}
                </tr>
                <tr>
                  <td className="border border-gray-300 p-4 font-semibold">Mileage</td>
                  {vehicles.map((vehicle) => (
                    <td key={vehicle._id} className="border border-gray-300 p-4 text-center">
                      {vehicle.performance.mileage_kmpl} kmpl
                    </td>
                  ))}
                </tr>
                <tr>
                  <td className="border border-gray-300 p-4 font-semibold">Rating</td>
                  {vehicles.map((vehicle) => (
                    <td key={vehicle._id} className="border border-gray-300 p-4 text-center">
                      <div className="flex items-center justify-center">
                        <span className="text-yellow-400">★</span>
                        <span className="ml-1">{vehicle.ratings.user_rating.toFixed(1)}</span>
                      </div>
                    </td>
                  ))}
                </tr>
                <tr>
                  <td className="border border-gray-300 p-4 font-semibold">Actions</td>
                  {vehicles.map((vehicle) => (
                    <td key={vehicle._id} className="border border-gray-300 p-4 text-center">
                      <Link
                        to={`/vehicle/${vehicle._id}`}
                        className="btn-primary text-sm"
                      >
                        View Details
                      </Link>
                    </td>
                  ))}
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      )}
    </div>
  );
};

export default ComparePage;
