import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

interface Vehicle {
  _id: string;
  brand: string;
  model: string;
  variant: string;
  category: string;
  fuel_type: string;
  pricing: {
    ex_showroom_inr: number;
    on_road_inr: number;
  };
  images: {
    thumbnail: string;
  };
  ratings: {
    user_rating: number;
  };
}

const HomePage: React.FC = () => {
  const [vehicles, setVehicles] = useState<Vehicle[]>([]);
  const [loading, setLoading] = useState(true);
  const [compareList, setCompareList] = useState<string[]>([]);
  const [showCompareModal, setShowCompareModal] = useState(false);
  const navigate = useNavigate();
  const [filters, setFilters] = useState({
    category: '',
    brand: '',
    fuel_type: '',
    min_price: '',
    max_price: '',
    search: '',
  });

  const fetchVehicles = useCallback(async () => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      Object.entries(filters).forEach(([key, value]) => {
        if (value) params.append(key, value);
      });

      const response = await axios.get(`http://localhost:5000/api/vehicles?${params}`);
      setVehicles(response.data);
    } catch (error) {
      console.error('Error fetching vehicles:', error);
    } finally {
      setLoading(false);
    }
  }, [filters]);

  useEffect(() => {
    fetchVehicles();
    // Load compare list from localStorage
    const savedCompareList = localStorage.getItem('compareList');
    if (savedCompareList) {
      setCompareList(JSON.parse(savedCompareList));
    }
  }, [fetchVehicles]);

  const handleFilterChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    setFilters({
      ...filters,
      [e.target.name]: e.target.value,
    });
  };

  const handleAddToCompare = (vehicleId: string) => {
    if (compareList.includes(vehicleId)) {
      // Remove from compare list
      const newCompareList = compareList.filter(id => id !== vehicleId);
      setCompareList(newCompareList);
      localStorage.setItem('compareList', JSON.stringify(newCompareList));
    } else {
      // Add to compare list
      if (compareList.length >= 4) {
        setShowCompareModal(true);
        return;
      }
      const newCompareList = [...compareList, vehicleId];
      setCompareList(newCompareList);
      localStorage.setItem('compareList', JSON.stringify(newCompareList));
    }
  };


  const handleCompareNow = () => {
    if (compareList.length >= 2) {
      navigate('/compare');
    }
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(price);
  };

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900 mb-4">Browse Vehicles</h1>
        
        {/* Filters */}
        <div className="bg-white p-6 rounded-lg shadow-md mb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Category</label>
              <select
                name="category"
                value={filters.category}
                onChange={handleFilterChange}
                className="input-field"
              >
                <option value="">All Categories</option>
                <option value="Bike">Bike</option>
                <option value="Scooter">Scooter</option>
                <option value="EV">Electric Vehicle</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Brand</label>
              <select
                name="brand"
                value={filters.brand}
                onChange={handleFilterChange}
                className="input-field"
              >
                <option value="">All Brands</option>
                <option value="Honda">Honda</option>
                <option value="Yamaha">Yamaha</option>
                <option value="TVS">TVS</option>
                <option value="Ola">Ola</option>
                <option value="Royal Enfield">Royal Enfield</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Fuel Type</label>
              <select
                name="fuel_type"
                value={filters.fuel_type}
                onChange={handleFilterChange}
                className="input-field"
              >
                <option value="">All Fuel Types</option>
                <option value="Petrol">Petrol</option>
                <option value="Electric">Electric</option>
                <option value="Diesel">Diesel</option>
              </select>
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Min Price</label>
              <input
                type="number"
                name="min_price"
                value={filters.min_price}
                onChange={handleFilterChange}
                placeholder="Min price"
                className="input-field"
              />
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Max Price</label>
              <input
                type="number"
                name="max_price"
                value={filters.max_price}
                onChange={handleFilterChange}
                placeholder="Max price"
                className="input-field"
              />
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">Search</label>
              <input
                type="text"
                name="search"
                value={filters.search}
                onChange={handleFilterChange}
                placeholder="Search vehicles..."
                className="input-field"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Vehicles Grid */}
      {loading ? (
        <div className="flex justify-center items-center py-12">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {vehicles.map((vehicle) => (
            <div key={vehicle._id} className="card hover:shadow-lg transition-shadow">
              <div className="aspect-w-16 aspect-h-9 mb-4">
                <img
                  src={vehicle.images.thumbnail}
                  alt={`${vehicle.brand} ${vehicle.model}`}
                  className="w-full h-48 object-cover rounded-lg"
                />
              </div>
              
              <div className="mb-4">
                <h3 className="text-xl font-semibold text-gray-900">
                  {vehicle.brand} {vehicle.model}
                </h3>
                <p className="text-gray-600">{vehicle.variant}</p>
                <p className="text-sm text-gray-500 capitalize">
                  {vehicle.category} • {vehicle.fuel_type}
                </p>
              </div>
              
              <div className="flex items-center justify-between mb-4">
                <div>
                  <p className="text-2xl font-bold text-primary-600">
                    {formatPrice(vehicle.pricing.ex_showroom_inr)}
                  </p>
                  <p className="text-sm text-gray-500">Ex-showroom</p>
                </div>
                <div className="text-right">
                  <div className="flex items-center">
                    <span className="text-yellow-400">★</span>
                    <span className="ml-1 text-sm text-gray-600">
                      {vehicle.ratings.user_rating.toFixed(1)}
                    </span>
                  </div>
                </div>
              </div>
              
              <div className="flex space-x-2">
                <Link
                  to={`/vehicle/${vehicle._id}`}
                  className="flex-1 btn-primary text-center"
                >
                  View Details
                </Link>
                <button 
                  onClick={() => handleAddToCompare(vehicle._id)}
                  className={`btn-secondary ${
                    compareList.includes(vehicle._id) 
                      ? 'bg-green-500 text-white hover:bg-green-600' 
                      : ''
                  }`}
                >
                  {compareList.includes(vehicle._id) ? 'Remove' : 'Compare'}
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
      
      {!loading && vehicles.length === 0 && (
        <div className="text-center py-12">
          <p className="text-gray-500 text-lg">No vehicles found matching your criteria.</p>
        </div>
      )}

      {/* Compare Button */}
      {compareList.length > 0 && (
        <div className="fixed bottom-6 right-6 z-50">
          <div className="bg-white rounded-lg shadow-lg p-4 border-2 border-primary-500">
            <div className="flex items-center space-x-4">
              <div>
                <p className="text-sm font-medium text-gray-900">
                  {compareList.length} vehicle{compareList.length > 1 ? 's' : ''} selected
                </p>
                <p className="text-xs text-gray-500">Max 4 vehicles</p>
              </div>
              <button
                onClick={handleCompareNow}
                disabled={compareList.length < 2}
                className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Compare Now
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Compare Modal */}
      {showCompareModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
            <h3 className="text-lg font-semibold mb-4">Maximum 4 vehicles can be compared</h3>
            <p className="text-gray-600 mb-4">
              Please remove a vehicle from your comparison list to add a new one.
            </p>
            <div className="flex space-x-3">
              <button
                onClick={() => setShowCompareModal(false)}
                className="flex-1 btn-secondary"
              >
                Cancel
              </button>
              <button
                onClick={() => {
                  setShowCompareModal(false);
                  navigate('/compare');
                }}
                className="flex-1 btn-primary"
              >
                Go to Compare
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default HomePage;
