import React, { useState, useEffect, useCallback } from 'react';
import { useParams, Link, useNavigate } from 'react-router-dom';
import axios from 'axios';

// Mock data for vehicle details
const mockVehicle = {
  _id: 'mock1',
  brand: 'Honda',
  model: 'Activa 6G',
  variant: 'DLX',
  category: 'Scooter',
  fuel_type: 'Petrol',
  year: 2023,
  pricing: {
    ex_showroom_inr: 70000,
    on_road_inr: 75000
  },
  images: {
    thumbnail: 'https://imgd.aeplcdn.com/664x374/n/cw/ec/1/versions/--connected-obd-2b1737697110094.jpg?q=80',
    gallery: [
      'https://imgd.aeplcdn.com/664x374/n/cw/ec/1/versions/--connected-obd-2b1737697110094.jpg?q=80',
      'https://imgd.aeplcdn.com/1056x594/n/cw/ec/1/versions/--connected-obd-2b1737697110094.jpg?q=80',
      'https://imgd.aeplcdn.com/1056x594/n/cw/ec/1/versions/--connected-obd-2b1737697110094.jpg?q=80'
    ]
  },
  ratings: {
    user_rating: 4.5,
    expert_rating: 4.3,
    reviews_count: 1250
  },
  engine: {
    capacity_cc: 110,
    max_power_bhp: 7.65,
    max_torque_nm: 8.79
  },
  performance: {
    top_speed_kmph: 85,
    mileage_kmpl: 45
  },
  features: {
    safety: ['Combi-Brake System', 'Tubeless Tyres', 'Side Stand Engine Cut-off'],
    comfort: ['Mobile Charging Port', 'External Fuel Filler', 'Boot Light'],
    technology: ['Digital Speedometer', 'LED Headlamp', 'Idle Start Stop System']
  },
  availability: {
    in_stock: true,
    delivery_time_days: 3
  }
};

interface Vehicle {
  _id: string;
  brand: string;
  model: string;
  variant: string;
  category: string;
  fuel_type: string;
  year: number;
  pricing: {
    ex_showroom_inr: number;
    on_road_inr: number;
  };
  images: {
    thumbnail: string;
    gallery: string[];
  };
  ratings: {
    user_rating: number;
    expert_rating: number;
    reviews_count: number;
  };
  engine?: {
    capacity_cc: number;
    max_power_bhp: number;
    max_torque_nm: number;
  };
  battery?: {
    capacity_kwh: number;
    range_km: number;
    charging_time_hours: number;
  };
  performance: {
    top_speed_kmph: number;
    mileage_kmpl: number;
  };
  features: {
    safety: string[];
    comfort: string[];
    technology: string[];
  };
  availability: {
    in_stock: boolean;
    delivery_time_days: number;
  };
}

const VehicleDetailPage: React.FC = () => {
  const { id } = useParams<{ id: string }>();
  const [vehicle, setVehicle] = useState<Vehicle | null>(null);
  const [loading, setLoading] = useState(true);
  const [selectedImage, setSelectedImage] = useState(0);
  const [compareList, setCompareList] = useState<string[]>([]);
  const [showCompareModal, setShowCompareModal] = useState(false);
  const navigate = useNavigate();

  const fetchVehicle = useCallback(async () => {
    try {
      setLoading(true);
      // Set mock data immediately
      setVehicle(mockVehicle as Vehicle);
      
      // Then try to fetch from API
      try {
        const response = await axios.get(`http://localhost:5000/api/vehicles/${id}`);
        if (response.data) {
          setVehicle(response.data);
        }
      } catch (error) {
        console.error('Error fetching vehicle from API, using mock data:', error);
      }
    } finally {
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchVehicle();
    // Load compare list from localStorage
    const savedCompareList = localStorage.getItem('compareList');
    if (savedCompareList) {
      setCompareList(JSON.parse(savedCompareList));
    }
  }, [fetchVehicle]);


  const handleAddToCompare = (vehicleId: string) => {
    if (compareList.includes(vehicleId)) {
      // Remove from compare list
      const newCompareList = compareList.filter(id => id !== vehicleId);
      setCompareList(newCompareList);
      localStorage.setItem('compareList', JSON.stringify(newCompareList));
    } else {
      // Add to compare list
      if (compareList.length >= 4) {
        setShowCompareModal(true);
        return;
      }
      const newCompareList = [...compareList, vehicleId];
      setCompareList(newCompareList);
      localStorage.setItem('compareList', JSON.stringify(newCompareList));
    }
  };

  const handleCompareNow = () => {
    if (compareList.length >= 2) {
      navigate('/compare');
    }
  };

  const formatPrice = (price: number) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      maximumFractionDigits: 0,
    }).format(price);
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!vehicle) {
    return (
      <div className="text-center py-12">
        <h2 className="text-2xl font-bold text-gray-900 mb-4">Vehicle not found</h2>
        <Link to="/home" className="btn-primary">Back to Browse</Link>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Images */}
        <div>
          <div className="aspect-w-16 aspect-h-9 mb-4">
            <img
              src={vehicle.images.gallery[selectedImage] || vehicle.images.thumbnail}
              alt={`${vehicle.brand} ${vehicle.model}`}
              className="w-full h-96 object-cover rounded-lg"
            />
          </div>
          
          {vehicle.images.gallery.length > 1 && (
            <div className="grid grid-cols-4 gap-2">
              {vehicle.images.gallery.map((image, index) => (
                <button
                  key={index}
                  onClick={() => setSelectedImage(index)}
                  className={`aspect-w-1 aspect-h-1 rounded-lg overflow-hidden ${
                    selectedImage === index ? 'ring-2 ring-primary-500' : ''
                  }`}
                >
                  <img
                    src={image}
                    alt={`${vehicle.brand} ${vehicle.model} ${index + 1}`}
                    className="w-full h-20 object-cover"
                  />
                </button>
              ))}
            </div>
          )}
        </div>

        {/* Details */}
        <div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            {vehicle.brand} {vehicle.model}
          </h1>
          <p className="text-xl text-gray-600 mb-4">{vehicle.variant}</p>
          
          <div className="flex items-center mb-4">
            <div className="flex items-center">
              <span className="text-yellow-400 text-xl">â˜…</span>
              <span className="ml-1 text-lg font-semibold">
                {vehicle.ratings?.user_rating?.toFixed(1) || 'N/A'}
              </span>
              <span className="ml-2 text-gray-500">
                ({vehicle.ratings?.reviews_count || 0} reviews)
              </span>
            </div>
          </div>

          <div className="bg-gray-50 p-6 rounded-lg mb-6">
            <h3 className="text-xl font-semibold mb-4">Pricing</h3>
            <div className="space-y-2">
              <div className="flex justify-between">
                <span>Ex-showroom Price:</span>
                <span className="font-semibold">{vehicle.pricing?.ex_showroom_inr ? formatPrice(vehicle.pricing.ex_showroom_inr) : 'N/A'}</span>
              </div>
              <div className="flex justify-between">
                <span>On-road Price:</span>
                <span className="font-semibold text-primary-600">
                  {vehicle.pricing?.on_road_inr ? formatPrice(vehicle.pricing.on_road_inr) : 'N/A'}
                </span>
              </div>
            </div>
          </div>

          <div className="space-y-4 mb-6">
            <button className="w-full btn-primary">
              Book Test Ride
            </button>
            <button 
              onClick={() => handleAddToCompare(vehicle._id)}
              className={`w-full btn-secondary ${
                compareList.includes(vehicle._id) 
                  ? 'bg-green-500 text-white hover:bg-green-600' 
                  : ''
              }`}
            >
              {compareList.includes(vehicle._id) ? 'Remove from Compare' : 'Add to Compare'}
            </button>
          </div>

          <div className="text-sm text-gray-600">
            <p className="mb-2">
              <span className="font-semibold">Availability:</span>{' '}
              {vehicle.availability.in_stock ? 'In Stock' : 'Out of Stock'}
            </p>
            {vehicle.availability.in_stock && (
              <p>
                <span className="font-semibold">Delivery:</span>{' '}
                {vehicle.availability.delivery_time_days} days
              </p>
            )}
          </div>
        </div>
      </div>

      {/* Specifications */}
      <div className="mt-12">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Specifications</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          <div className="card">
            <h3 className="text-lg font-semibold mb-4">Basic Info</h3>
            <div className="space-y-2">
              <div className="flex justify-between">
                <span>Category:</span>
                <span className="font-medium">{vehicle.category}</span>
              </div>
              <div className="flex justify-between">
                <span>Fuel Type:</span>
                <span className="font-medium">{vehicle.fuel_type}</span>
              </div>
              <div className="flex justify-between">
                <span>Year:</span>
                <span className="font-medium">{vehicle.year}</span>
              </div>
            </div>
          </div>

          {vehicle.engine && (
            <div className="card">
              <h3 className="text-lg font-semibold mb-4">Engine</h3>
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Capacity:</span>
                  <span className="font-medium">{vehicle.engine.capacity_cc}cc</span>
                </div>
                <div className="flex justify-between">
                  <span>Power:</span>
                  <span className="font-medium">{vehicle.engine.max_power_bhp} bhp</span>
                </div>
                <div className="flex justify-between">
                  <span>Torque:</span>
                  <span className="font-medium">{vehicle.engine.max_torque_nm} Nm</span>
                </div>
              </div>
            </div>
          )}

          {vehicle.battery && (
            <div className="card">
              <h3 className="text-lg font-semibold mb-4">Battery</h3>
              <div className="space-y-2">
                <div className="flex justify-between">
                  <span>Capacity:</span>
                  <span className="font-medium">{vehicle.battery.capacity_kwh} kWh</span>
                </div>
                <div className="flex justify-between">
                  <span>Range:</span>
                  <span className="font-medium">{vehicle.battery.range_km} km</span>
                </div>
                <div className="flex justify-between">
                  <span>Charging Time:</span>
                  <span className="font-medium">{vehicle.battery.charging_time_hours} hrs</span>
                </div>
              </div>
            </div>
          )}

          <div className="card">
            <h3 className="text-lg font-semibold mb-4">Performance</h3>
            <div className="space-y-2">
              <div className="flex justify-between">
                <span>Top Speed:</span>
                <span className="font-medium">{vehicle.performance.top_speed_kmph} kmph</span>
              </div>
              <div className="flex justify-between">
                <span>Mileage:</span>
                <span className="font-medium">{vehicle.performance.mileage_kmpl} kmpl</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Features */}
      <div className="mt-12">
        <h2 className="text-2xl font-bold text-gray-900 mb-6">Features</h2>
        
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="card">
            <h3 className="text-lg font-semibold mb-4">Safety</h3>
            <ul className="space-y-2">
              {vehicle.features.safety.map((feature, index) => (
                <li key={index} className="flex items-center">
                  <span className="text-green-500 mr-2">âœ“</span>
                  {feature}
                </li>
              ))}
            </ul>
          </div>

          <div className="card">
            <h3 className="text-lg font-semibold mb-4">Comfort</h3>
            <ul className="space-y-2">
              {vehicle.features.comfort.map((feature, index) => (
                <li key={index} className="flex items-center">
                  <span className="text-green-500 mr-2">âœ“</span>
                  {feature}
                </li>
              ))}
            </ul>
          </div>

          <div className="card">
            <h3 className="text-lg font-semibold mb-4">Technology</h3>
            <ul className="space-y-2">
              {vehicle.features.technology.map((feature, index) => (
                <li key={index} className="flex items-center">
                  <span className="text-green-500 mr-2">âœ“</span>
                  {feature}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>

      {/* Compare Button */}
      {compareList.length > 0 && (
        <div className="fixed bottom-6 right-6 z-50">
          <div className="bg-white rounded-lg shadow-lg p-4 border-2 border-primary-500">
            <div className="flex items-center space-x-4">
              <div>
                <p className="text-sm font-medium text-gray-900">
                  {compareList.length} vehicle{compareList.length > 1 ? 's' : ''} selected
                </p>
                <p className="text-xs text-gray-500">Max 4 vehicles</p>
              </div>
              <button
                onClick={handleCompareNow}
                disabled={compareList.length < 2}
                className="btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
              >
                Compare Now
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Compare Modal */}
      {showCompareModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
          <div className="bg-white rounded-lg p-6 max-w-md w-full mx-4">
            <h3 className="text-lg font-semibold mb-4">Maximum 4 vehicles can be compared</h3>
            <p className="text-gray-600 mb-4">
              Please remove a vehicle from your comparison list to add a new one.
            </p>
            <div className="flex space-x-3">
              <button
                onClick={() => setShowCompareModal(false)}
                className="flex-1 btn-secondary"
              >
                Cancel
              </button>
              <button
                onClick={() => {
                  setShowCompareModal(false);
                  navigate('/compare');
                }}
                className="flex-1 btn-primary"
              >
                Go to Compare
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default VehicleDetailPage;
